package week2.day2;

public class ConvertANegativeNumberToPositiveNumber {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=-40;
		if(i<0) {
			System.out.println(i+" is a negative number");
		int a = i*(-1);
			System.out.println("Positive number is " +a);
		}
	}

}
