package week2.day2;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1-20;
			System.out.println("Printing only the odd numbers from 1 to 20");
			for(i=1; i < 20; i++) {
				if (i%2!=0) {
					System.out.println(i+"");
				}
				
			}
	
	}
}
